mapper.update:
update cbbs
set title = #{title},
	content = #{content}


	,fname =#{fname},
	filesize= #{filesize}


where cnum = #{cnum}

update cbbs 
set ccount=ccount+1 
where cnum= #{cnum}

mapper.select:
 	select cnum,title,content,
 	to_char(cdate,'yyyy-mm-dd hh24:mi:ss')cdate,fname,ccount,id,urllink,r
from(
		select cnum,title,content,cdate,fname,ccount,id,urllink,rownum r
	from(                                                      
			select cnum,title,content,cdate,fname,ccount,id,urllink
			from(
			select *from cbbs                                                  
			
			
				
					id like '%' || #{word} || '%'
				
				
					title like '%' || #{word} || '%'
				
				
					content like '%' || #{word} || '%'
				
			
			
			order by cnum DESC
			
			)
	 	)
	)
    
    where r>=#{sno} and r <=#{eno}
