mapper.update:
    update pbbs set
	content = #{content}
	where bnum = #{bnum}

mapper.select:
 	SELECT bnum, bname, content, bdate, r
 		from(
 			select bnum, bname, content, bdate, rownum r
 			from (
 				select bnum, bname, content, to_char(bdate, 'yyyy-mm-dd')bdate
 				from pbbs
 	
	
		
		bname like '%' || #{word} || '%'
		
		
		content like '%' || #{word} || '%'
		
	
	   
	order by bnum DESC            
		)
	)
	
	where r>=#{sno} and r<=#{eno}

	select count(*) from pbbs
    where bnum=#{bnum} and 
    passwd=#{passwd}
